import "./featuredInfo.css";
import { ArrowDownward, ArrowUpward } from "@material-ui/icons";
import { useEffect } from "react";
import axios from "axios";

export default function FeaturedInfo() {
  let totalMonth, amount, totalLastMonth;
  useEffect(() => {
    const getStats = async () => {
      try {
        const res = await axios.get("/movies/stats", {
          headers: {
            token:
              "header eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxNmUzN2QwNzM2ZThkNGVkYmRkYWNmMiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYzNDY1MzEzMSwiZXhwIjoxNjM1MDg1MTMxfQ.r-ywhBHKf3DzjwYps7PmqF5Cjga4REv4_opjxbGBi8I",
          },
        });
        const statsMovieList = res.data;
        const thisMonth = new Date().getMonth();
        statsMovieList.map((item) => {
          if (item._id == thisMonth - 1) {
            totalLastMonth = item.total;
          }
          if (item._id == thisMonth) {
            totalMonth = item.total;
            amount = (totalMonth - totalLastMonth) / 100;
            amount.toFixed(2);
          }
        });
        getStats();
        console.log(totalMonth, totalLastMonth, amount);
      } catch (error) {
        console.log({ error });
      }
    };
    getStats();
  }, []);
  return (
    <div className="featured">
      <div className="featuredItem">
        <span className="featuredTitle">Movie</span>
        <div className="featuredMoneyContainer">
          <span className="featuredMoney">{totalMonth}</span>
          <span className="featuredMoneyRate">
            {amount}{" "}
            {amount < 0 ? (
              <ArrowDownward className="featuredIcon negative" />
            ) : (
              <ArrowUpward className="featuredIcon" />
            )}
          </span>
        </div>
        <span className="featuredSub">Compared to last month</span>
      </div>
      <div className="featuredItem">
        <span className="featuredTitle">User</span>
        <div className="featuredMoneyContainer">
          <span className="featuredMoney">$4,415</span>
          <span className="featuredMoneyRate">
            -1.4 <ArrowDownward className="featuredIcon negative" />
          </span>
        </div>
        <span className="featuredSub">Compared to last month</span>
      </div>
      <div className="featuredItem">
        <span className="featuredTitle">List</span>
        <div className="featuredMoneyContainer">
          <span className="featuredMoney">$2,225</span>
          <span className="featuredMoneyRate">
            +2.4 <ArrowUpward className="featuredIcon" />
          </span>
        </div>
        <span className="featuredSub">Compared to last month</span>
      </div>
    </div>
  );
}
