import {
  Add,
  PlayArrow,
  ThumbDownAltOutlined,
  ThumbUpAltOutlined,
} from "@material-ui/icons";
import { useState } from "react";
import "./listitem.scss";

const ListItem = ({ index }) => {
  const [isHovered, setIsHovered] = useState(false);
  const trailer = "https://youtu.be/J9vFB65-U_c";
  return (
    <div
      className="listItem"
      style={{ left: isHovered && index * 225 - 50 + index * 2.5 }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <img
        src="https://c4.wallpaperflare.com/wallpaper/226/891/767/movies-hollywood-movies-wallpaper-preview.jpg"
        alt=""
      />
      <video src={trailer} autoPlay={true} loop />

      <div className="itemInfo">
        <div className="icons">
          <PlayArrow />
          <Add />
          <ThumbUpAltOutlined />
          <ThumbDownAltOutlined />
        </div>
        <div className="itemInfoTop">
          <span>1h14m</span>
          <span className="limit">16+</span>
          <span>1999</span>
        </div>
        <div className="desc">
          Lorem ipsum dolor sit, amet consectetur adipisicing elit. Corrupti
          quod, quae quasi molestiae illo aut explicabo veniam maiores
          voluptatibus suscipit cumque commodi, provident sit doloribus facere,
          dicta perferendis voluptas cum?
        </div>
        <div className="genre">Action</div>
      </div>
    </div>
  );
};

export default ListItem;
