import {
  Add,
  PlayArrow,
  ThumbDownAltOutlined,
  ThumbUpAltOutlined,
} from "@material-ui/icons";
import { useEffect, useState } from "react";
import "./listitem.scss";
import axios from "axios"

const ListItem = ({ index, item }) => {
  const [isHovered, setIsHovered] = useState(false);
  // const trailer =
  //   "https://www.flexclip.com/res/preview/720p/suspense-trailer.mp4";
  useEffect(()=>{
    const getMovie = async()=>{
      try {
        const res = await axios.get('movies/find/'+item,{
          
        })
      } catch (error) {
        
      }
    }
  })
  return (
    <div
      className="listItem"
      style={{ left: isHovered && index * 225 - 50 + index * 2.5 }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* src="https://c4.wallpaperflare.com/wallpaper/226/891/767/movies-hollywood-movies-wallpaper-preview.jpg" */}
      <img src={item.img} alt="" />
      {isHovered && (
        <div>
          <video src={item.trailer} autoPlay={true} loop muted />
          <div className="itemInfo">
            <div className="icons">
              <PlayArrow className="icon" />
              <Add className="icon" />
              <ThumbUpAltOutlined className="icon" />
              <ThumbDownAltOutlined className="icon" />
            </div>
            <div className="itemInfoTop">
              <span>{item.duration} minutes</span>
              <span className="limit">{item.limit}+</span>
              <span>{item.year}</span>
            </div>
            <div className="desc">{item.desc}</div>
            <div className="genre">{item.genre}</div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListItem;
